import Vue from "vue";
import Vuex from "vuex";

import adiyl from "../assets/image/adiyl.jpeg";
import bayer from "../assets/image/bayer.jpeg";
import check from "../assets/image/check.jpeg";
import chronfecle from "../assets/image/chronfecle.jpeg";
import ddrug from "../assets/image/ddrug.jpg";
import drug from "../assets/image/drug.jpeg";
import drug2 from "../assets/image/drug2.jpeg";
import drug3 from "../assets/image/drug3.jpeg";
import drug4 from "../assets/image/drug4.jpeg";
import drug5 from "../assets/image/drug5.jpeg";
import equator from "../assets/image/equator.jpeg";
import gavinsco from "../assets/image/gavinsco.jpeg";
import hab from "../assets/image/hab.jpeg";
import paracetamol from "../assets/image/paracetamol.jpeg";
import pil from "../assets/image/pil.jpeg";
import prilose from "../assets/image/prilose.jpeg";
import rayAid from "../assets/image/rayAid.jpeg";
import sinaspril from "../assets/image/sinaspril.jpeg";
import suboxine from "../assets/image/suboxine.jpeg";
import tidinDrug from "../assets/image/tidinDrug.jpeg";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    topRated: [
      {
        id: 1,
        name: "Adiyl",
        price: 45,
        url: adiyl,
        description: "Very Good",
        rate: 3.1,
      },
      {
        id: 2,
        name: "Bayer",
        price: 45,
        url: bayer,
        description: "Very Good",
        rate: 3.1,
      },
      {
        id: 3,
        name: "Check",
        price: 45,
        url: check,
        description: "Very Good",
        rate: 3.1,
      },
    ],
    allCategories: [
      {
        id: 4,
        name: "Chronfecle",
        price: 67,
        url: chronfecle,
        description: "Awesome",
        rate: 4.1,
      },
      {
        id: 5,
        name: "Chrofemanic",
        price: 45,
        url: drug,
        description: "Very Good",
        rate: 3.1,
      },
      {
        id: 6,
        name: "drug2",
        price: 45,
        url: drug2,
        description: "Very Good",
        rate: 3.1,
      },
    ],
    highQuality: [
      {
        id: 7,
        name: "drug3",
        price: 45,
        url: drug3,
        description: "Very Good",
        rate: 3.1,
      },
      {
        id: 8,
        name: "drug4",
        price: 45,
        url: drug4,
        description: "Very Good",
        rate: 3.1,
      },
      {
        id: 9,
        name: "Check",
        price: 45,
        url: check,
        description: "Very Good",
        rate: 3.1,
      },
    ],
    cartCount: 0,
    cartItem: [],
  },
  getters: {},
  mutations: {
    addToCart(state, payload) {
      let item = payload;
      item = { ...item, quantity: 1 };
      if (state.cartItem.length > 0) {
        let check = state.cartItem.some((i) => i.id === item.id);

        if (check) {
          let itemIndex = state.cartItem.findIndex((element) => element.id === item.id);
          state.cartItem[itemIndex]["quantity"] += 1;
        } else {
          state.cartItem.push(item);
        }
      } else {
        state.cartItem.push(item);
      }
      state.cartCount++;
    },
    removeItem(state, payload){
      if(state.cartItem.length > 0){
        let bool = state.cartItem.some(i => i.id === payload.id)
        if(bool){
          let itemIndex = state.cartItem.findIndex(element => element.id === payload.id)
          if(state.cartItem[itemIndex]["quantity"] !== 0){
            state.cartItem[itemIndex]["quantity"] -= 1;
            state.cartCount--;
          }
           if(state.cartItem[itemIndex]["quantity"] == 0){
            state.cartItem.splice(itemIndex, 1)
           }
        }
      }
    }
  },
  actions: {
    addToCart: (context, payload) => {
      context.commit("addToCart", payload);
    },
    removeItem: (context, payload) => {
      context.commit("removeItem", payload);
    },
  },
  modules: {},
});
