import Vue from 'vue'
import Vuex from 'vuex'

import adiyl from '../assets/image/adiyl.jpeg'
import bayer from "../assets/image/bayer.jpeg";
import check from "../assets/image/check.jpeg";
import chronfecle from "../assets/image/chronfecle.jpeg";
import ddrug from "../assets/image/ddrug.jpeg";
import drug from "../assets/image/drug.jpeg";
import drug2 from "../assets/image/drug2.jpeg";
import drug3 from "../assets/image/drug3.jpeg";
import drug4 from "../assets/image/drug4.jpeg";
import drug5 from "../assets/image/drug5.jpeg";
import equator from "../assets/image/equator.jpeg";
import gavinsco from "../assets/image/gavinsco.jpeg";







Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    names: [
      {id: 1, name: 'Bolaji', age: 20, status: 'single', average: 'better'},
      {id: 2, name: 'Hammed', age: 22, status: 'date on', average: 'stable'},
      {id: 3, name: 'Adekunle', age: 23, status: 'married', average: 'wealthy'},
    ],
    count: 0
  },
  getters: {
    getName: state => {
      return state.names.filter(item => item.name) 
    }
  },
  mutations: {
    increment(state, payload){
      state.count += payload
    }
    //the mutation can be hard to debugg when using a async operation
     // increment(state){
    //   setTimeout(() => {
    //     state.count++
    //   }, 3000) 
    // }
  },
  actions: {
    increment: (context, payload) => {
      setTimeout(() => {
        context.commit('increment', payload)
        console.log(payload);
        console.log('actions');
      }, 3000)
    }
  },
  modules: {
  }
})
