import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    names: [
      {id: 1, name: 'Bolaji', age: 20, status: 'single', average: 'better'},
      {id: 2, name: 'Hammed', age: 22, status: 'date on', average: 'stable'},
      {id: 3, name: 'Adekunle', age: 23, status: 'married', average: 'wealthy'},
    ],
    count: 0
  },
  getters: {
    getName: state => {
      return state.names.filter(item => item.name) 
    }
  },
  mutations: {
    increment(state){
      state.count++
    }
    //the mutation can be hard to debugg when using a async operation
     // increment(state){
    //   setTimeout(() => {
    //     state.count++
    //   }, 3000) 
    // }
  },
  actions: {
    increment: (context, payload) => {
      setTimeout(() => {
        context.commit('increment')
      })
    }
  },
  modules: {
  }
})
